//import React from "react";
import * as React from "react";
import { jsPlumb } from "jsplumb";

const diagram = {
  height: "90vh",
  position: "relative",
};
const control1 = {
  left: "50px",
  top: "50px",
};
const control2 = {
  left: "300px",
  top: "200px",
};
class Graph extends React.Component {
  componentDidMount() {
    // function uuidv4() {
    //   return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(
    //     /[xy]/g,
    //     function (c) {
    //       var r = (Math.random() * 16) | 0,
    //         v = c == "x" ? r : (r & 0x3) | 0x8;
    //       return v.toString(16);
    //     }
    //   );
    // }
    const instance = jsPlumb.getInstance({});
    instance.setContainer("diagram");
    instance.bind("ready", function () {
      // task 1.2 dragable elements
      instance.draggable("control1", { containment: true });
      instance.draggable("control2", { containment: true });

      instance.addEndpoint("control1", {
        endpoint: "Dot",
        anchor: ["RightMiddle"],
        isSource: true,
        // connectionType: "red-connection"
      });
      instance.addEndpoint("control2", {
        endpoint: "Dot",
        anchor: ["LeftMiddle"],
        isTarget: true,
        //connectionType: "red-connection",
      });
      $("#toolbox .control").draggable({
        helper: "clone",
        containment: "body",
        appendTo: "#diagram",
      });

      $("#diagram").droppable({
        drop: function (event, ui) {
          var id = uuidv4();
          var clone = $(ui.helper).clone(true);
          clone.attr("id", id);
          clone.appendTo(this);
          instance.draggable(id, { containment: true });

          instance.addEndpoint(id, {
            endpoint: "Dot",
            anchor: ["RightMiddle"],
            isSource: true,
            connectionType: "red-connection",
          });

          instance.addEndpoint(id, {
            endpoint: "Dot",
            anchor: ["LeftMiddle"],
            isTarget: true,
            connectionType: "red-connection",
          });
        },
      });
    });
  }

  render() {
    return (
      <React.Fragment>
        <div class="container-fluid">
          <div class="row">
            <div class="col-md-3">
              <div id="toolbox" class="justify-content-center">
                <div class="control new">New Control</div>
              </div>
            </div>
            <div class="col-md-9">
              <div id="diagram" style={diagram}>
                <div id="control1" class="control" style={control1}>
                  Control 1
                </div>
                <div id="control2" class="control" style={control2}>
                  Control 2
                </div>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Graph;
