//import React from "react";
import * as React from "react";
import { jsPlumb } from "jsplumb";
import $ from "jquery";
//import { v4 as uuidv4 } from "uuid";

const diagram = {
  height: "90vh",
  position: "relative",
};
const control1 = {
  left: "50px",
  top: "50px",
};
const control2 = {
  left: "300px",
  top: "200px",
};
const control3 = {
  left: "400px",
  top: "300px",
};
class Graph extends React.Component {
  componentDidMount() {
    const instance = jsPlumb.getInstance({});
    instance.setContainer("diagram");
    instance.bind("ready", function () {
      instance.registerConnectionTypes({
        "red-connection": {
          paintStyle: { stroke: "blue", strokeWidth: 5 },
          hoverPaintStyle: { stroke: "blue", strokeWidth: 10 },
          connector: "Flowchart",
        },
      });

      // task 1.2 dragable elements
      instance.draggable("control1", { containment: true });
      instance.draggable("control2", { containment: true });

      // Connect elements visually
      instance.addEndpoint("control1", {
        endpoint: "Dot",
        anchor: ["RightMiddle"],
        isSource: true,
        connectionType: "red-connection",
      });
      instance.addEndpoint("control2", {
        endpoint: "Dot",
        anchor: ["LeftMiddle"],
        isTarget: true,

        maxConnections: 2, //connect to multiple endpoints
        connectionType: "red-connection",

        // maximum connection alert
        onMaxConnections: function (info, e) {
          alert("Maximum connections reached");
        },
      });
      instance.addEndpoint("control3", {
        endpoint: "Dot",
        anchor: ["RightMiddle"],
        isSource: true,
        connectionType: "red-connection",
      });

      //delete all connection on click
      $("body").on("click", document.getElementById("endd"), function (e) {
        alert("deleting connections");
        instance.deleteEveryConnection();
      });

      // run a function upon connection established
      instance.bind("connection", function (info, originalEvent) {
        alert("connection established");
      });

      // Task delete connection
      instance.bind("contextmenu", function (component, event) {
        if (component.hasClass("jtk-connector")) {
          event.preventDefault();
          window.selectedConnection = component;
          $(
            "<div class='custom-menu'><button class='delete-connection'>Delete connection</button></div>"
          )
            .appendTo("body")
            .css({ top: event.pageY + "px", left: event.pageX + "px" });
        }
      });
      $("body").on("click", ".delete-connection", function (event) {
        //delete the line connection
        instance.deleteConnection(window.selectedConnection);
      });
      //remove the created button
      $(document).bind("click", function (event) {
        $("div.custom-menu").remove();
      });

      // delete control
      $("body").on("contextmenu", "#diagram .control", function (event) {
        event.preventDefault();
        window.selectedControl = $(this).attr("id");
        $(
          "<div class='custom-menu'><button class='delete-control'>Delete control</button></div>"
        )
          .appendTo("body")
          .css({ top: event.pageY + "px", left: event.pageX + "px" });
      });
      $("body").on("click", ".delete-control", function (event) {
        instance.remove(window.selectedControl);
      });
      // $("li.list-group-item").draggable({
      //   helper: "clone",
      //   zIndex: 100,
      //   scroll: false,
      //   start: function (event, ui) {
      //     var width = event.target.getBoundingClientRect().width;
      //     $(ui.helper).css({
      //       width: Math.ceil(width),
      //     });
      //   },
      // });
      // $("#toolbox .control").draggable({
      //   helper: "clone",
      //   containment: "body",
      //   appendTo: "#diagram",
      // });
    });
  }
  componentWillUnmount() {}

  render() {
    return (
      <React.Fragment>
        <div class="container-fluid">
          <div class="row">
            <div class="col-md-3">
              <div id="toolbox" class="justify-content-center">
                <div id="end" class="control search">
                  Search
                </div>
                <button id="endd" onclick="click()">
                  click me
                </button>
              </div>
              <h4>List Items (Draggable)</h4>
              <ul class="list-group">
                <li class="list-group-item">
                  <div class="draggable field">Cras justo</div>
                </li>
                <li class="list-group-item">
                  <div class="draggable field">Dapibus ac</div>
                </li>
                <li class="list-group-item">
                  <div class="draggable field">Morbi leo</div>
                </li>
                <li class="list-group-item">
                  <div class="draggable field">Porta ac</div>
                </li>
                <li class="list-group-item">
                  <div class="draggable field">Vestibulum</div>
                </li>
              </ul>
            </div>
            <div class="col-md-9">
              <div id="diagram" style={diagram}>
                <div id="control1" class="control" style={control1}>
                  Control 1
                </div>
                <div id="control2" class="control" style={control2}>
                  Control 2
                </div>
                <div id="control3" class="control" style={control3}>
                  Control 2
                </div>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Graph;
