import * as React from "react";
import { jsPlumb } from "jsplumb";

const style = {
  width: 100,
  height: 100,
  background: "#e0e0e0",
};

class Graph extends React.Component {
  componentDidMount() {
    const j = jsPlumb.getInstance();
    j.draggable(["el1", "el2"]);
    j.connect({
      source: "el1",
      target: "el2",
    });
  }

  render() {
    return (
      <React.Fragment>
        <div class="container-fluid">
          <div class="row">
            <div class="col-md-3">
              <div id="toolbox" class="justify-content-center"></div>
            </div>
            <div class="col-md-9">
              <div id="diagram" style={diagram}>
                <div id="control1" class="control" style={control1}>
                  Control 1
                </div>
                <div id="control2" class="control" style={control2}>
                  Control 2
                </div>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Graph;
