import React from "react";
import jsPlumb from "jsplumb/dist/js/jsplumb.min";
import "./jsplumbdemo.css";
const diagram = {
  height: "90vh",
  position: "relative",
};
const control1 = {
  left: "50px",
  top: "50px",
};
const control2 = {
  left: "300px",
  top: "200px",
};

export default class MyJsPlumb extends React.Component {
  componentDidMount() {
    jsPlumb.getInstance({});
    jsPlumb.setContainer("diagram");
    jsPlumb.bind("ready", function () {
      // task 1.2 dragable elements
      jsPlumb.draggable("control1", { containment: true });
      jsPlumb.draggable("control2", { containment: true });

      jsPlumb.addEndpoint("control1", {
        endpoint: "Dot",
        anchor: ["RightMiddle"],
        isSource: true,
        // connectionType: "red-connection"
      });
      jsPlumb.addEndpoint("control2", {
        endpoint: "Dot",
        anchor: ["LeftMiddle"],
        isTarget: true,
        //connectionType: "red-connection",
      });
    });

    return (
      <div class="container-fluid">
        <div class="row">
          <div class="col-md-3">
            <div id="toolbox" class="justify-content-center"></div>
          </div>
          <div class="col-md-9">
            <div id="diagram" style={diagram}>
              <div id="control1" class="control" style={control1}>
                Control 1
              </div>
              <div id="control2" class="control" style={control2}>
                Control 2
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
